import ReactDOM from "react-dom";
import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { BrowserRouter, Route, Routes, Switch } from "react-router-dom";

import "../css/main.css"

export default function Header(){
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(''); 
    const [reportText, setReportText] = useState('');

    const openModal = () => {
        setModalOpen(true);
    }

    const closeModal = () => {
        setModalOpen(false);
    }

    const handleOptionClick = (option) => {
        setSelectedOption(option);
        closeModal();
    }

    const handleComplete = () => {
        // axios를 통해 서버로 결과값 전달
        console.log('Selected Option :', selectedOption);
        console.log('Report Text:', reportText);
        closeModal();
    }

    return (
      <div>
        <header>
          <nav className="bg-white border-gray-200 px-4 lg:px-6 py-2.5 dark:bg-gray-800">
            <div className="flex flex-wrap justify-between items-center mx-auto max-w-screen-xl">
              <a href="/" className="flex items-center">
                <span className="self-center text-xl font-semibold whitespace-nowrap dark:text-white">Safety Hawkeye</span>
              </a>
              <div className="flex items-center lg:order-2">
                <button onClick={openModal} className="text-white bg-primary-700 hover:bg-primary-800 focus:ring-4 focus:ring-primary-300 font-medium rounded-lg text-sm px-4 lg:px-5 py-2 lg:py-2.5 mr-2 dark:bg-primary-600 dark:hover:bg-primary-700 focus:outline-none dark:focus:ring-primary-800">신고 및 제보하기</button>
              </div>
              <div className="hidden justify-between items-center w-full lg:flex lg:w-auto lg:order-1" id="mobile-menu-2">
                {/* ... */}
              </div>
            </div>
          </nav>
        </header>
  
        {isModalOpen && (
          <div className="modal fixed inset-0 flex justify-center items-center bg-gray-700 bg-opacity-75">
            <div className="modal-content bg-white w-11/12 md:w-1/2 lg:w-1/3 p-6 rounded-lg overflow-y-auto">
              <h2 className="text-xl font-semibold mb-4">문의하기</h2>
              <p>긴급 상황엔 제출만 눌러도 됩니다</p> <br/>
              <a className="text-red-500">허위 신고의 경우 서비스 이용의 제재나 법적 처벌이 이루어질 수 있습니다.</a>
              {selectedOption === 'yes' && (
                <div className="mt-4">
                  <textarea
                    rows="4"
                    className="w-full p-2 border rounded-md mb-4"
                    placeholder="신고할 내용을 입력하세요"
                    value={reportText}
                    onChange={(e) => setReportText(e.target.value)}
                  ></textarea>
                   <div className="flex flex-col md:flex-row md:justify-between">
                      <button onClick={handleComplete} className="block md:w-1/4 mx-auto px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md mb-2 md:mb-0">제출</button>
                      <button onClick={closeModal} className="block md:w-1/4 mx-auto px-4 py-2 bg-blue-300 hover:bg-blue-500 text-white rounded-md">닫기</button>
                    </div>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    );
  }
