import ReactDOM from "react-dom";
import React from "react";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import { BrowserRouter, Route, Routes, Switch } from "react-router-dom";

import "../css/main.css"

export default function Header(){
    const [isModalOpen, setModalOpen] = useState(false);
    const [selectedOption, setSelectedOption] = useState(''); 
    const [reportText, setReportText] = useState('');

    const openModal = () => {
        setModalOpen(true);
    }

    const closeModal = () => {
        setModalOpen(false);
    }

    const handleOptionClick = (option) => {
        setSelectedOption(option);
        closeModal();
    }

    const handleComplete = () => {
        // axios를 통해 서버로 결과값 전달
        console.log('Selected Option :', selectedOption);
        console.log('Report Text:', reportText);
        closeModal();
    }

    return (
<div>
<header>
    <nav className="bg-white border-gray-200 px-4 lg:px-6 py-2.5 dark:bg-gray-800">
        <div className="flex flex-wrap justify-between items-center mx-auto max-w-screen-xl">
            <a href="/" className="flex items-center">
                {/* <img src="https://flowbite.com/docs/images/logo.svg" className="mr-3 h-6 sm:h-9" alt="Flowbite Logo" /> */}
                <span className="self-center text-xl font-semibold whitespace-nowrap dark:text-white">Safety Hawkeye</span>
            </a>
            <div className="flex items-center lg:order-2">
                <button onClick={openModal} className="text-white bg-primary-700 hover:bg-primary-800 focus:ring-4 focus:ring-primary-300 font-medium rounded-lg text-sm px-4 lg:px-5 py-2 lg:py-2.5 mr-2 dark:bg-primary-600 dark:hover:bg-primary-700 focus:outline-none dark:focus:ring-primary-800">신고 및 제보하기</button>

            </div>
            <div className="hidden justify-between items-center w-full lg:flex lg:w-auto lg:order-1" id="mobile-menu-2">
      <ul className="flex flex-col mt-4 font-medium lg:flex-row lg:space-x-8 lg:mt-0">
        <li>
          <a href="#" className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 lg:hover:bg-transparent lg:border-0 lg:hover:text-primary-700 lg:p-0 dark:text-gray-400 lg:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white lg:dark:hover:bg-transparent dark:border-gray-700">지도</a>
        </li>
        <li>
          <a href="#" className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 lg:hover:bg-transparent lg:border-0 lg:hover:text-primary-700 lg:p-0 dark:text-gray-400 lg:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white lg:dark:hover:bg-transparent dark:border-gray-700">관리자</a>
        </li>
        <li>
          <button onClick={openModal} className="block py-2 pr-4 pl-3 text-gray-700 border-b border-gray-100 hover:bg-gray-50 lg:hover:bg-transparent lg:border-0 lg:hover:text-primary-700 lg:p-0 dark:text-gray-400 lg:dark:hover:text-white dark:hover:bg-gray-700 dark:hover:text-white lg:dark:hover:bg-transparent dark:border-gray-700">문의하기</button>
          {isModalOpen && (
            <div className="modal fixed inset-0 flex justify-center items-center bg-gray-700 bg-opacity-75">
              <div className="modal-content bg-white w-1/2 p-6 rounded-lg">
                <a>Email : abc@gmail.com</a><br/>
                <a>유선 연락처 : 010-1234-5678</a>
                <button onClick={closeModal} className="block mx-auto mt-4 px-4 py-2 bg-blue-500 hover:bg-blue-600 text-white rounded-md">닫기</button>
              </div>
            </div>
          )}
        </li>
      </ul>
    </div>
        </div>
    </nav>
</header>
</div>
    )
}
